                    #     user_data = load_user_data_from_id(copytradetxhash[i]['user_id'])
                        
                    #     await fee_transfer(web3, amount, copytradetxhash[i]['user_id'], user_data.wallet_private_key)
                    # except Exception as e:
                    #     print(f"fee transfer fail {str(e)}")
          
